def unambiguity_score(d,n2e,e2n): # weighted Bernoulli Trials
    if d<=0:
        return 0
    if d>300:
        return  unambiguity_score(300,n2e,e2n)
    prob_beneficial  = .5 #+ math.log(d,2)/d
    prob_detrimental = 1 - prob_beneficial
    
    p = prob_beneficial
    q = prob_detrimental
    ambiguity = []
    for k in range(0,d+1,1):

        dCk          = nchoosek(d,k,exact=True) 
        prob         = dCk   *   p**k   *   q**(d-k) # od dCk * p**d if p=q
        
        ###################################################################
        ambiguity.append(n2e*(prob**(e2n*log10(d)))) # winner
        ###################################################################

    return np.average(ambiguity)
